import"./hoisted.DWZJc7hw.js";document.addEventListener("DOMContentLoaded",()=>{const a=document.getElementById("budgetingForm"),d=document.getElementById("budgetResults");a.addEventListener("submit",i=>{i.preventDefault();const t=parseFloat(document.getElementById("monthlyIncome").value),l=parseFloat(document.getElementById("housingCost").value),m=parseFloat(document.getElementById("transportationCost").value),u=parseFloat(document.getElementById("foodCost").value),c=parseFloat(document.getElementById("utilitiesCost").value),r=parseFloat(document.getElementById("savingsGoal").value),o=l+m+u+c,s=t-o,n=r/t*100;document.getElementById("totalIncomeResult").textContent=`$${t.toFixed(2)}`,document.getElementById("totalExpensesResult").textContent=`$${o.toFixed(2)}`,document.getElementById("remainingBudgetResult").textContent=`$${s.toFixed(2)}`,document.getElementById("savingsPercentageResult").textContent=`${n.toFixed(2)}%`;const e=document.getElementById("budgetRecommendations");s<0?e.textContent="⚠️ Your expenses exceed your income. Consider reducing costs or finding additional income sources.":n<10?e.textContent="💡 Try to increase your savings. Aim for at least 10-20% of your monthly income.":n>=20?e.textContent="🌟 Great job! You're on track with your savings goals.":e.textContent="💰 You have a balanced budget. Continue monitoring your expenses.",d.classList.remove("hidden")})});
