import"./hoisted.DWZJc7hw.js";document.addEventListener("DOMContentLoaded",()=>{const s=document.getElementById("debtRepaymentForm"),l=document.getElementById("debtResults");s.addEventListener("submit",d=>{d.preventDefault();const o=parseFloat(document.getElementById("totalDebt").value),i=parseFloat(document.getElementById("interestRate").value)/100/12,r=parseFloat(document.getElementById("monthlyPayment").value);parseFloat(document.getElementById("minimumPayment").value);let t=o,e=0,n=0;for(;t>0;){const m=t*i;n+=m;const c=Math.min(r,t+m);if(t=Math.max(0,t+m-c),e++,e>600)break}const u=o+n;document.getElementById("monthsToRepayResult").textContent=`${e} months`,document.getElementById("totalInterestResult").textContent=`$${n.toFixed(2)}`,document.getElementById("totalAmountPaidResult").textContent=`$${u.toFixed(2)}`;const a=document.getElementById("debtRecommendations");e>120?a.textContent="⚠️ Your debt repayment will take over 10 years. Consider debt consolidation or increasing monthly payments.":n>o*.5?a.textContent="💡 You're paying a significant amount in interest. Look into balance transfer options or negotiating lower rates.":a.textContent="🌟 You have a solid debt repayment strategy. Stay consistent with your payments.",l.classList.remove("hidden")})});
