import"./hoisted.DWZJc7hw.js";document.addEventListener("DOMContentLoaded",()=>{const s=document.getElementById("retirementForm"),i=document.getElementById("retirementResults");s.addEventListener("submit",m=>{m.preventDefault();const a=parseFloat(document.getElementById("currentAge").value),l=parseFloat(document.getElementById("retirementAge").value),d=parseFloat(document.getElementById("currentSavings").value),u=parseFloat(document.getElementById("monthlyContribution").value),c=parseFloat(document.getElementById("expectedReturn").value)/100;parseFloat(document.getElementById("annualInflation").value)/100;const n=l-a;let e=d;for(let r=0;r<n;r++)e=(e+u*12)*(1+c);const o=e*.04/12;document.getElementById("totalSavingsResult").textContent=`$${e.toFixed(2)}`,document.getElementById("yearsToRetirementResult").textContent=`${n} years`,document.getElementById("monthlyIncomeResult").textContent=`$${o.toFixed(2)}`;const t=document.getElementById("retirementRecommendations");n<15?t.textContent="⚠️ You have limited time to save. Consider maximizing contributions and exploring catch-up strategies.":o<3e3?t.textContent="💡 Your projected retirement income seems low. Look into increasing contributions or exploring additional income sources.":e<5e5?t.textContent="🌱 You're on the right track, but continue to look for ways to increase your savings and investments.":t.textContent="🌟 Great job! You're making solid progress towards your retirement goals.",i.classList.remove("hidden")})});
